// ------------------------ListTour.js--------------------------
import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import Navbar from "../WebPage/Navbar";
import Footer from "../WebPage/Footer";

export default function ListTour() {
    const [selectedLocation, setSelectedLocation] = useState('Tất cả');
    const [startDate, setStartDate] = useState('');
    const [departure, setDeparture] = useState('');
    const products = useSelector(state => state.products);
    const dispatch = useDispatch();
    const location = useLocation();
    const [sortCriterion, setSortCriterion] = useState('');
    

    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const locationParam = params.get('location');
        const startDateParam = params.get('startDate');
        const departureParam = params.get('departure');
        
        if (locationParam) setSelectedLocation(locationParam);
        if (startDateParam) setStartDate(startDateParam);
        if (departureParam) setDeparture(departureParam);
    }, [location]);

    const handleLocationChange = (event) => {
        setSelectedLocation(event.target.value);
    };

    const handleSortChange = (criterion) => {
        setSortCriterion(criterion);
    };

    const filteredProducts = products.filter(product => {
        const matchLocation = selectedLocation === 'Tất cả' || product.tour.includes(selectedLocation);
        const matchStartDate = !startDate || product.startDate === startDate;
        const matchDeparture = !departure || product.departure.includes(departure);
        return matchLocation && matchStartDate && matchDeparture;
    });
    const sortedProducts = [...filteredProducts];
    if (sortCriterion === 'time') {
        sortedProducts.sort((a, b) => a.time.localeCompare(b.time));
    } else if (sortCriterion === 'startDate') {
        sortedProducts.sort((a, b) => new Date(a.schedule) - new Date(b.schedule));
    } else if (sortCriterion === 'price') {
        sortedProducts.sort((a, b) => parseFloat(a.price.replace(/\./g, '')) - parseFloat(b.price.replace(/\./g, '')));
    }

    return (
        <div>
            <Navbar />
            <div className="container mt-4">
                <h1 className="mb-4">Tìm kiếm tour du lịch</h1>
                <div className="row">
                    <div className="col-md-3">
                        {/* Bộ lọc sản phẩm */}
                        <div className="card">
                            <h5 className="card-header">Bộ lọc</h5>
                            <div className="card-body">
                                {/* Lọc theo địa điểm */}
                                <div className="form-group">
                                    <label htmlFor="location">Địa điểm:</label>
                                    <select className="form-control" id="location" value={selectedLocation} onChange={handleLocationChange}>
                                        <option>Tất cả</option>
                                        <option>Hà Nội</option>
                                        <option>Đà Nẵng</option>
                                        <option>Phú Quốc</option>
                                        <option>Trung Quốc</option>
                                        {/* Thêm các tùy chọn địa điểm khác */}
                                    </select>
                                </div>

                                {/* Lọc theo thời gian */}
                                <div className="form-group">
                                    <label htmlFor="duration">Thời gian:</label>
                                    <select className="form-control" id="duration" value={startDate} onChange={handleLocationChange}>
                                        <option>Tất cả</option>
                                        <option>3 ngày 2 đêm</option>
                                        <option>17/06/2024</option>
                                        <option>Cuối tuần</option>
                                        {/* Thêm các tùy chọn thời gian khác */}
                                    </select>
                                </div>

                                {/* Lọc theo giá tour */}
                                <div className="form-group">
                                    <label>Giá tour:</label>
                                    <div className="form-check">
                                        <input className="form-check-input" type="checkbox" id="price1" />
                                        <label className="form-check-label" htmlFor="price1">
                                            Dưới 1 triệu
                                        </label>
                                    </div>
                                    <div className="form-check">
                                        <input className="form-check-input" type="checkbox" id="price2" />
                                        <label className="form-check-label" htmlFor="price2">
                                            Từ 1-5 triệu
                                        </label>
                                    </div>
                                    {/* Thêm các tùy chọn giá tour khác */}
                                </div>

                                <button type="button" className="btn btn-primary" onClick={() => setSelectedLocation(selectedLocation)}>Áp dụng bộ lọc</button>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-9">

                        <div className="d-flex justify-content-evenly">
                            <a className="btn btn-outline-primary">Thời lượng tour</a>
                            <a className="btn btn-outline-primary">Ngày khởi hành</a>
                            <a className="btn btn-outline-primary">Giá Tour</a>
                        </div>

                        <h2>Danh sách sản phẩm</h2>
                        {filteredProducts.map(product => (
                            <Product 
                                key={product.id}
                                id={product.id}
                                name={product.name}
                                time={product.time}
                                image={product.image}
                                schedule={product.schedule}
                                location={product.location}
                                content={product.content}
                                price={product.price}
                                start={product.start}
                                image1={product.image1}
                                point={product.point}
                                tour={product.tour}
                                isBuying={product.isBuying}
                            />
                        ))}
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

function Product({ id, name, time, image, schedule, price, color, isBuying, point }) {
    const dispatch = useDispatch();

    const changeColor = () => {
        if (isBuying) {
            dispatch({ type: 'cart.minus', payload: { product: { id } } });
        } else {
            dispatch({ type: 'cart.add', payload: { product: { id, name, time, image, schedule, price, color, isBuying } } });
        }
    };
    return (
        <div className="card mb-3" style={{ maxWidth: '1000px' }}>
            <div className="row g-0">
                <div className="col-md-4">
                    <img src={image} className="img-fluid" alt={name} style={{ height: "-webkit-fill-available" }} />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>
                        <p className="card-text"><i className="bi bi-calendar"></i> {schedule}</p>
                        <p className="card-text"><i className="bi bi-clock"></i> {time}</p>
                        <p className="card-text text-success fw-bolder">{point}</p>
                        <div className="text-end d-flex justify-content-between">
                            <p className="card-text fs-3 text-warning fw-bold .mt-0">{price} VNĐ</p>
                            <div className="d-flex justify-content-around" style={{ width: '120px' }}>
                                <a
                                    onClick={changeColor}
                                    className={`btn text-center p-2 pl-2 pr-2 ${color === 'red' ? 'btn-danger' : 'btn-primary'}`}
                                >
                                    {isBuying ? "LOẠI BỎ" : "THÊM"}
                                </a>
                                <Link to={`/product/${id}`} className="btn btn-success p-2 pl-2 pr-2">
                                    XEM
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
