import { useLoaderData, useNavigate } from "react-router-dom";
import productData from "../data/ProductData";
import { useState } from "react";

async function getProduct(id) {
    return productData.find((product) => product.id == id);
}

export async function loadProduct({ params }) {
    const product = await getProduct(params.id);
    console.log(product);
    return product;
}

export default function ProductDetail() {
    const product = useLoaderData();
    const navigate = useNavigate();

    const [adults, setAdults] = useState(1);
    const [children, setChildren] = useState(0);

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <section id="productDetails" className="pb-5">
            <div>
                <button onClick={() => navigate('/list-product')} className="btn btn-primary">BACK</button>
            </div>
            <div className="container mt-4">
                <div className="row">
                    <div className="col-md-8">
                        <h1>{product.name}</h1>
                        <div className="position-relative">
                            <img src={product.image} alt={product.name} className="img-fluid" />
                        </div>
                        <p className="mt-2">{product.description}</p>
                    </div>
                    <div className="col-md-4">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">Lịch khởi hành & giá</h5>
                                <div className="form-group">
                                    <label htmlFor="date-select">Chọn ngày khởi hành:</label>
                                    <select id="date-select" className="form-control">
                                        <option>23/06/2023</option>
                                        <option>30/06/2023</option>
                                        <option>14/07/2023</option>
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>Người lớn:</label>
                                    <input 
                                        type="number" 
                                        className="form-control" 
                                        value={adults} 
                                        onChange={(e) => setAdults(e.target.value)} 
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Trẻ em:</label>
                                    <input 
                                        type="number" 
                                        className="form-control" 
                                        value={children} 
                                        onChange={(e) => setChildren(e.target.value)} 
                                    />
                                </div>
                                <p className="text-muted">Liên hệ để xác nhận chỗ</p>
                                <p className="font-weight-bold">Tổng cộng: 1.600.000 VND</p>
                                <button type="button" className="btn btn-warning w-100 mb-2">Liên hệ tư vấn</button>
                                <button type="button" className="btn btn-primary w-100">Thêm vào giỏ hàng</button>
                            </div>
                        </div>
                        <ul className="list-unstyled mt-3">
                            <li>Bảo hiểm</li>
                            <li>Xe đưa đón</li>
                            <li>Bữa ăn</li>
                            <li>Hướng dẫn viên</li>
                            <li>Khách sạn</li>
                        </ul>
                    </div>
                </div>
            </div>
        </section>
    );
}
