import { useLoaderData, useNavigate } from "react-router-dom";
import productData from "../data/ProductData";
import { useState } from "react";

async function getProduct(id) {
    return productData.find((product) => product.id == id);
}

export async function loadProduct({ params }) {
    const product = await getProduct(params.id);
    console.log(product);
    return product;
}

export default function ProductDetail() {
    const product = useLoaderData();
    const navigate = useNavigate();

    const [adults, setAdults] = useState(2);
    const [children, setChildren] = useState(0);
    const [infants, setInfants] = useState(0);

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <section id="productDetails" className="pb-5">
            <div>
                <button onClick={() => navigate('/list-product')} className="btn btn-primary">BACK</button>
            </div>
            <div className="container mt-4">
                <div className="row">
                    <div className="col-md-8">
                        <h1>{product.name}</h1>
                        <div className="position-relative">
                            <img src={product.image} alt={product.name} className="tour-image" />
                            <div className="price-banner">Ưu Đãi Cực Hấp Dẫn | 12.330K/khách</div>
                        </div>
                        <p className="mt-2">{product.description}</p>
                        <div className="thumbnail-images">
                            {product.thumbnails.map((thumb, index) => (
                                <img key={index} src={thumb} alt={`Thumbnail ${index + 1}`} />
                            ))}
                        </div>
                    </div>
                    <div className="col-md-4 contact-info">
                        <h4>Lịch Trình và Giá Tour</h4>
                        <form>
                            <div className="form-group">
                                <label htmlFor="date-select">Chọn Lịch Trình và Xem Giá:</label>
                                <select id="date-select" className="form-control">
                                    <option>23/06</option>
                                    <option>30/06</option>
                                    <option>14/07</option>
                                    <option>Tất cả</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Người lớn:</label>
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    value={adults} 
                                    onChange={(e) => setAdults(e.target.value)} 
                                />
                            </div>
                            <div className="form-group">
                                <label>Trẻ em (2-10 tuổi):</label>
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    value={children} 
                                    onChange={(e) => setChildren(e.target.value)} 
                                />
                            </div>
                            <div className="form-group">
                                <label>Trẻ nhỏ:</label>
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    value={infants} 
                                    onChange={(e) => setInfants(e.target.value)} 
                                />
                            </div>
                            <p>Giá gốc: <s>25.900.000 VND</s></p>
                            <p className="price">Tổng Giá Tour: 24.660.000 VND</p>
                            <button type="button" className="btn btn-primary">Đặt Tour ngay</button>
                        </form>
                        <div className="mt-3">
                            <span className="star-rating">★ ★ ★ ★ ☆</span> 4.6 (32)
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}
