import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

export default function ListTour() {
    const products = useSelector(state => state.products); // Assuming 'products' is an array in Redux state
    const cart = useSelector(state => state.cart); // Assuming 'cart' is an array or object in Redux state
    const dispatch = useDispatch();

    const addToCart = (product) => {
        if (product.isBuying) {
            dispatch({ type: 'cart.minus', payload: { product: { id: product.id } } });
        } else {
            dispatch({ type: 'cart.add', payload: { product } });
        }
    };

    return (
        <div className="container mt-4">
            <h1 className="mb-4">Tìm kiếm tour du lịch</h1>
            <div className="row">
                <div className="col-md-3">
                    <div className="card">
                        <h5 className="card-header">Bộ lọc</h5>
                        <div className="card-body">
                            {/* Filter options */}
                        </div>
                    </div>
                </div>
                <div className="col-md-9">
                    <h2>Danh sách sản phẩm</h2>
                    {products.map(product => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </div>
            </div>
        </div>
    );
}

function Product({ product, addToCart }) {
    const { id, name, time, image, schedule, price, color, isBuying } = product;

    return (
        <div className="card mb-3" style={{ maxWidth: '540px' }}>
            <div className="row g-0">
                <div className="col-md-4">
                    <img src={image} className="img-fluid rounded-start" alt={name} />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>
                        <p className="card-text">{schedule}</p>
                        <p className="card-text">{price}</p>
                        <button className="btn btn-primary" onClick={() => addToCart(product)}>
                            {isBuying ? 'Remove from Cart' : 'Add to Cart'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}
