// -----------------------RootReducer.js-----------------------
import { createReducer } from '@reduxjs/toolkit';

const initState = {
    products: [],
    cart: checkCart(),
};

export const root = createReducer(initState, (builder) => {
    builder
        .addCase('product.load', (state, action) => {
            const products = action.payload.products;
            const cart = checkCart();
            const out = products.map(p => {
                const isBuying = cart.some(c => c.id === p.id);
                return { ...p, isBuying, color: isBuying ? 'red' : 'blue' };
            });
            state.products = out;
        })
        .addCase('cart.add', (state, action) => {
            const product = action.payload.product;
            state.cart = [...state.cart, product];
            saveCart(state.cart);
        })
        .addCase('cart.minus', (state, action) => {
            const productId = action.payload.product.id;
            state.cart = state.cart.filter(product => product.id !== productId);
            saveCart(state.cart);
        });
});

function checkCart() {
    return JSON.parse(localStorage.getItem('cart')) || [];
}

function saveCart(cart) {
    localStorage.setItem('cart', JSON.stringify(cart));
}

