import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

export default function ListTour() {
    const products = useSelector(state => state.products);
    const cart = useSelector(state => state.cart);

    return (
        <div>
            <div className="container mt-4">
                <h1 className="mb-4">Tìm kiếm tour du lịch</h1>
                <div className="row">
                <div className="col-md-3">
                    {/* Bộ lọc sản phẩm */}
                    <div className="card">
                        <h5 className="card-header">Bộ lọc</h5>
                        <div className="card-body">
                            {/* Lọc theo địa điểm */}
                            <div className="form-group">
                                <label htmlFor="location">Địa điểm:</label>
                                <select className="form-control" id="location">
                                    <option>Tất cả</option>
                                    <option>Hà Nội</option>
                                    <option>Đà Nẵng</option>
                                    <option>Phú Quốc</option>
                                    {/* Thêm các tùy chọn địa điểm khác */}
                                </select>
                            </div>

                            {/* Lọc theo thời gian */}
                            <div className="form-group">
                                <label htmlFor="duration">Thời gian:</label>
                                <select className="form-control" id="duration">
                                    <option>Tất cả</option>
                                    <option>3 ngày 2 đêm</option>
                                    <option>Mùa hè 2024</option>
                                    <option>Cuối tuần</option>
                                    {/* Thêm các tùy chọn thời gian khác */}
                                </select>
                            </div>

                            {/* Lọc theo giá tour */}
                            <div className="form-group">
                                <label>Giá tour:</label>
                                <div className="form-check">
                                    <input className="form-check-input" type="checkbox" id="price1" />
                                    <label className="form-check-label" htmlFor="price1">
                                        Dưới 1 triệu
                                    </label>
                                </div>
                                <div className="form-check">
                                    <input className="form-check-input" type="checkbox" id="price2" />
                                    <label className="form-check-label" htmlFor="price2">
                                        Từ 1-5 triệu
                                    </label>
                                </div>
                                {/* Thêm các tùy chọn giá tour khác */}
                            </div>

                            <button type="button" className="btn btn-primary">Áp dụng bộ lọc</button>
                        </div>
                    </div>
                </div>
                    <div className="col-md-9">
                        {products.map(product => (
                            <Product 
                                key={product.id}
                                id={product.id}
                                name={product.name}
                                time={product.time}
                                image={product.image}
                                schedule={product.schedule}
                                location={product.location}
                                content={product.content}
                                price={product.price}
                                start ={product.start}
                                image1={product.image1}


                                url={product.url}
                                votes={product.votes}
                                submitterAvatarUrl={product.submitterAvatarUrl}
                                color={product.color}
                                isBuying={product.isBuying}
                            />
                        ))}
                    </div>
                </div>
            </div>
        </div> 
    );

}
function Product({ id, name, time, image, schedule, price, color, isBuying }) {
    const dispatch = useDispatch();

    const changeColor = () => {
        if (isBuying) {
            dispatch({ type: 'cart.minus', payload: { product: { id } } });
        } else {
            dispatch({ type: 'cart.add', payload: { product: { id, name, time, image, schedule, price, color, isBuying } } });
        }
    };
    return(
        <div>     
                    {/* Danh sách sản phẩm */}
                    <h2>Danh sách sản phẩm</h2>
                    <div className="row">
                        {/* Mỗi sản phẩm du lịch sẽ được hiển thị ở đây */}
                        <div className="col-lg-4 col-md-6 mb-4">
                            <div className="card h-100">
                                <img className="card-img-top" src={image} alt="Ảnh tour" />
                                <div className="card-body">
                                    <h4 className="card-title">{name}</h4>
                                    <p className="card-text"><i className="bi bi-clock"></i> {time}</p>
                    <p className="card-text"><i className="bi bi-calendar-event"></i> {schedule}</p>
                    <p className="card-text text-warning fs-4 fw-bolder">{price} VNĐ</p>
                                </div>
                                <a
                                    onClick={changeColor}
                                    className={`btn text-center p-2 pl-2 pr-2 ${color === 'red' ? 'btn-danger' : 'btn-primary'}`}>
                                    {isBuying ? "LOẠI BỎ" : "THÊM"}
                                </a>
                                <div className="card-footer">
                                    <a href="#" className="btn btn-primary">Xem chi tiết</a>
                                </div>
                            </div>
                        </div>
                        {/* Thêm các sản phẩm du lịch khác tương tự */}
                    </div>
    </div>

    );
}
